	let displayIcon = icons[name];

	// Function to handle the viewBox input; defaults to icon.box if not provided
	function getViewBox() {
		if (viewBoxValues.length === 4) {
			// If the user provides a custom viewBox array
			return viewBoxValues.join(' ');
		}
		// Default viewBox (e.g., "0 0 24 24")
		return `0 0 ${displayIcon.box} ${displayIcon.box}`;
	}

	// Helper function to append 'rem' if the input is a number or a string without units
	function formatToRem(value: string | number): string {
		// If it's a number, or if it's a string representing a number (no units), append 'rem'
		if (typeof value === 'number' || /^[0-9.]+$/.test(value)) {
			return `${value}rem`;
		}
		// Otherwise, return the value as is (it may already contain units like 'px', '%', etc.)
		return value.toString();
	}
</script>

<svg
	class={$$props.class}
	{focusable}
	width={formatToRem(width)}
	height={formatToRem(height)}
	viewBox={getViewBox()}
	fill="none"
	>{@html displayIcon.svg}
</svg>

